sources.sass/components/_modal.scss:
.modal-order-service {
  position: relative;
  padding: 40px;
  background: rgba(17, 21, 24, 0.6);
  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
    0px 2px 1px rgba(0, 0, 0, 0.2);
  border-radius: 4px;
  overflow-y: auto;
  top: 12%;
}
.modal-close-btn {
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0;
  width: 30px;
  height: 30px;
  right: 8px;
  top: 8px;
  border: none;
  background-color: rgba(0, 0, 0, 0);
  cursor: pointer;
}
.modal-close-icon {
  fill: #fff;
  width: 30px;
  height: 30px;
  transition: $modal-time $modal-form;

  .modal-close-btn:focus &,
  .modal-close-btn:hover & {
    fill: $accent-color;
  }
}
@media screen and (min-width: 480px) {
  .modal-order-service {
    left: 50%;
    transform: translateX(-50%);
    width: 450px;
  }
}
@media screen and (min-width: 1024px) {
  .modal-order-service {
    top: 50%;
    transform: translate(-50%, -50%);
  }
}
@media screen and (min-width: 1200px) {
  .modal-order-service {
    width: 528px;
  }
}
//-----------------------------------
.modal-appeal {
  font-weight: bold;
  font-size: 20px;
  line-height: 1.5;
  text-align: center;
  color: $primary-white-color;
  margin-bottom: 12px;
}
/*---------form-------------------*/
.modal-form {
  display: flex;
  flex-direction: column;
}
.modal-form-field {
  font-size: 12px;
  line-height: 1.16;
  letter-spacing: 0.01em;
  &:nth-child(-n + 2) {
    margin-bottom: 5px;
  }
}
.modal-form-wrapper {
  margin-top: 4px;
  position: relative;
  display: block;
}
.modal-form-input {
  width: 100%;
  border: 1px solid rgba(33, 33, 33, 0.2);
  box-sizing: border-box;
  border-radius: 4px;
  height: 40px;
  padding-left: 42px;
  transition: border-color $modal-time $modal-form;
  &:focus {
    border-color: $accent-color;
    outline: none;
  }
}
.modal-form-icon {
  position: absolute;
  top: 50%;
  left: 12px;
  transform: translateY(-50%);
  display: block;
  width: 18px;
  height: 18px;
  transition: fill $modal-time $modal-form;
  .modal-form-input:focus + & {
    fill: $accent-color;
  }
}
.modal-form-text {
  margin-top: 4px;
  width: 100%;
  height: 120px;
  border: 1px solid rgba(33, 33, 33, 0.2);
  box-sizing: border-box;
  border-radius: 4px;
  padding: 12px 16px;
  resize: none;
  transition: border-color $modal-time $modal-form;
  &::placeholder {
    font-family: inherit;
    font-style: normal;
    font-weight: normal;
    font-size: 14px;
    line-height: 1.14;
    letter-spacing: 0.01em;
    color: rgba(117, 117, 117, 0.5);
  }
  &:focus {
    border-color: $accent-color;
    outline: none;
    fill: $accent-color;
  }
}
.modal-form-submit-btn {
  margin-left: auto;
  margin-right: auto;
  margin-top: 30px;
  border: none;
  width: 180px;
  min-height: 50px;
  background: #ff6b09e3;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.15);
  font-weight: bold;
  font-size: 16px;
  line-height: 1.88;
  letter-spacing: 0.08em;
  color: #ffffff;
  transition: background-color $modal-time $modal-form;
  &:focus,
  &:hover {
    background-color: $accent-color;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
  }
}
@media screen and (min-width: 1200px) {
  .modal-appeal {
    line-height: 1.15;
  }
}

sources.sass/components/_slider.scss:
.slick-list {
  overflow: hidden;
}
/* Лента слайдов */
.slick-track {
  display: flex;
}

.slider {
  position: relative;
  padding-right: 49px;
  padding-left: 49px;
}

.slider-item {
  display: flex;
  justify-content: center;
}

.slick-center {
  opacity: 1;
}

.slick-current .slider-image {
  border-radius: 50%;
  border: 2px solid $accent-color;
}

/* Стрелка */
.slick-arrow {
  height: 16px;
  width: 25px;
  font-size: 0px;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  z-index: 1;
  border: none;
  background-color: inherit;
  cursor: pointer;
}
/* Стрелка влево */
.slick-arrow.slick-prev {
  left: 0;
  background-image: url(../images/icons/arrow1.svg);
  background-repeat: no-repeat;
  background-size: 100%;
}
/* Стрелка вправо */
.slick-arrow.slick-next {
  right: 0;
  background-image: url(../images/icons/arrow2.svg);
  background-repeat: no-repeat;
  background-size: 100%;
}

sources.sass/layout/_header.scss:
.header {
  padding-top: 30px;
  padding-bottom: 30px;

  @media screen and (min-width: 768px) {
    padding-top: 40px;
    padding-bottom: 42px;
  }

  @media screen and (min-width: 1366px) {
    padding-top: 40px;
    padding-bottom: 23px;
  }
}

.header-container {
  justify-content: space-between;
  display: flex;
  align-items: center;
}
.header-nav-list,
.header-order-button {
  display: none;
}
.hamburger {
  position: relative;
}

//-------------tablet------------------------
@media screen and (min-width: 768px) {
  .header-order-button {
    display: block;
  }
  .header-btn-container {
    display: flex;
    align-items: center;
  }
  .header-order-button {
    font-size: 14px;
    letter-spacing: 0.14em;
    background-color: $primary-white-color;
    color: $accent-color;
    transition: $modal-time $modal-form;
    &:focus,
    &:hover {
      color: #fff;
      background-color: $accent-color;
      filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
    }
  }
  .header-mobile-button {
    margin-left: 40px;
  }
}
//--------------desktop---------------------
@media screen and (min-width: 1366px) {
  .header-mobile-button {
    display: none;
  }
  .button-heder-menu {
    min-width: 336px;
    min-height: 70px;
  }
  .header-nav-list {
    display: flex;
    margin-left: 114px;
  }
  .header-nav-item:not(:last-child) {
    margin-right: 40px;
  }
  .header-nav {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
  .header-nav-link {
    text-decoration: none;
    font-family: Montserrat;
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    line-height: 1.21;
    display: flex;
    align-items: center;
    text-align: center;
    text-transform: uppercase;
    color: $primary-text-color;
    transition: $modal-time $modal-form;
    &:hover,
    &:focus {
      filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
      color: $accent-color;
    }
  }
}
//-----------mobile menu----------------
.mobile-header {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: $backdrop;
  overflow: auto;
  z-index: 10;
  opacity: 0;
  visibility: hidden;
  pointer-events: none;
  transition: opacity $modal-time $backdrop-transition, visibility $modal-time $backdrop-transition;
}
.is-open {
  opacity: 1;
  visibility: visible;
  pointer-events: auto;
}
//----------------------------
.mobile-menu-logo {
  position: relative;
  z-index: 11;
  color: $primary-white-color;
  opacity: 1;
  visibility: visible;
  pointer-events: auto;
}
.mobile-list {
  display: flex;
  flex-direction: column;
  margin-bottom: 50px;
  width: fit-content;
  margin-left: auto;
  margin-right: auto;
}
.mobile-link {
  font-weight: 800;
  font-size: 20px;
  line-height: 1.8;
  letter-spacing: 0.03em;
  color: $primary-white-color;
  text-decoration: none;
  transition: $modal-time $modal-form;
  &:hover,
  &:focus {
    color: $accent-color;
  }
}
.mobile-header-container {
  position: relative;
  transform: translateX(-50%);
  top: 110px;
  left: 50%;
}
@media screen and (min-width: 480px) {
  .mobile-header-container {
    position: absolute;
    transform: translateX(-50%);
    top: 160px;
  }
}
@media screen and (min-width: 768px) {
  .button-mob-menu {
    display: none;
  }
  .mobile-link {
    font-size: 26px;
  }
}
//----------BURGER ANIMATION----------------------------
.hamburger {
  z-index: 11;
  padding: 10px 5px 10px 30px;
  display: inline-block;
  cursor: pointer;
  -webkit-transition-property: opacity, -webkit-filter;
  transition-property: opacity, -webkit-filter;
  transition-property: opacity, filter;
  transition-property: opacity, filter, -webkit-filter;
  -webkit-transition-duration: 0.15s;
  transition-duration: 0.15s;
  -webkit-transition-timing-function: linear;
  transition-timing-function: linear;
  font: inherit;
  color: inherit;
  text-transform: none;
  background-color: transparent;
  border: 0;
  margin: 0;
  overflow: visible;
  &:hover {
    opacity: 0.7;
  }
}
@media screen and (min-width: 1366px) {
  .hamburger {
    display: none;
  }
}
.hamburger.burger-active:hover {
  opacity: 0.7;
}
.hamburger.burger-active .hamburger-inner,
.hamburger.burger-active .hamburger-inner::before,
.hamburger.burger-active .hamburger-inner::after {
  background-color: #fff;
}
.hamburger-box {
  width: 40px;
  height: 24px;
  display: inline-block;
  position: relative;
}
.hamburger-inner {
  display: block;
  top: 50%;
  margin-top: -2px;
}
.hamburger-inner,
.hamburger-inner::before,
.hamburger-inner::after {
  width: 30px;
  height: 4px;
  background-color: #000;
  border-radius: 4px;
  position: absolute;
  -webkit-transition-property: -webkit-transform;
  transition-property: -webkit-transform;
  transition-property: transform;
  transition-property: transform, -webkit-transform;
  -webkit-transition-duration: 0.15s;
  transition-duration: 0.15s;
  -webkit-transition-timing-function: ease;
  transition-timing-function: ease;
}
.hamburger-inner::before,
.hamburger-inner::after {
  content: '';
  display: block;
}
.hamburger-inner::before {
  top: -10px;
}
.hamburger-inner::after {
  bottom: -10px;
}
.hamburger--emphatic {
  overflow: hidden;
}
.hamburger--emphatic .hamburger-inner {
  -webkit-transition: background-color 0.125s 0.175s ease-in;
  transition: background-color 0.125s 0.175s ease-in;
}
.hamburger--emphatic .hamburger-inner::before {
  left: 0;
  -webkit-transition: top 0.05s 0.125s linear, left 0.125s 0.175s ease-in,
    -webkit-transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335);
  transition: top 0.05s 0.125s linear, left 0.125s 0.175s ease-in,
    -webkit-transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335);
  transition: transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335), top 0.05s 0.125s linear,
    left 0.125s 0.175s ease-in;
  transition: transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335), top 0.05s 0.125s linear,
    left 0.125s 0.175s ease-in, -webkit-transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335);
}
.hamburger--emphatic .hamburger-inner::after {
  top: 10px;
  right: 0;
  -webkit-transition: top 0.05s 0.125s linear, right 0.125s 0.175s ease-in,
    -webkit-transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335);
  transition: top 0.05s 0.125s linear, right 0.125s 0.175s ease-in,
    -webkit-transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335);
  transition: transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335), top 0.05s 0.125s linear,
    right 0.125s 0.175s ease-in;
  transition: transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335), top 0.05s 0.125s linear,
    right 0.125s 0.175s ease-in, -webkit-transform 0.125s cubic-bezier(0.6, 0.04, 0.98, 0.335);
}
.hamburger--emphatic.burger-active .hamburger-inner {
  -webkit-transition-delay: 0s;
  transition-delay: 0s;
  -webkit-transition-timing-function: ease-out;
  transition-timing-function: ease-out;
  background-color: transparent !important;
}
.hamburger--emphatic.burger-active .hamburger-inner::before {
  left: -80px;
  top: -80px;
  -webkit-transform: translate3d(80px, 80px, 0) rotate(45deg);
  transform: translate3d(80px, 80px, 0) rotate(45deg);
  -webkit-transition: left 0.125s ease-out, top 0.05s 0.125s linear,
    -webkit-transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1);
  transition: left 0.125s ease-out, top 0.05s 0.125s linear,
    -webkit-transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1);
  transition: left 0.125s ease-out, top 0.05s 0.125s linear,
    transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1);
  transition: left 0.125s ease-out, top 0.05s 0.125s linear,
    transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1),
    -webkit-transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1);
}
.hamburger--emphatic.burger-active .hamburger-inner::after {
  right: -80px;
  top: -80px;
  -webkit-transform: translate3d(-80px, 80px, 0) rotate(-45deg);
  transform: translate3d(-80px, 80px, 0) rotate(-45deg);
  -webkit-transition: right 0.125s ease-out, top 0.05s 0.125s linear,
    -webkit-transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1);
  transition: right 0.125s ease-out, top 0.05s 0.125s linear,
    -webkit-transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1);
  transition: right 0.125s ease-out, top 0.05s 0.125s linear,
    transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1);
  transition: right 0.125s ease-out, top 0.05s 0.125s linear,
    transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1),
    -webkit-transform 0.125s 0.175s cubic-bezier(0.075, 0.82, 0.165, 1);
}

sources.sass/layout/_hero.scss:
.hero {
  padding: 20px 0 47px; // was 22 47
  background-repeat: no-repeat;
  background-position: left -64px bottom -25px;
  background-size: cover;

   background-image: url(../images/content/tablet/hero-bg-tab.png);

  @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
    background-image: url(../images/content/tablet/hero-bg-tab@2x.png);
  }

   @media screen and (min-width: 768px) {
      padding: 42px 0 64px;
      background-position: center bottom;
   }

   @media screen and (min-width: 1366px) {
      padding: 23px 0 10px; // was top 11
      background-image: url(../images/content/desktop/hero-bg-desk.png);

      @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
         background-image: url(../images/content/desktop/hero-bg-desc@2x.png);
      }
   }
}

.hero-container {
   @media screen and (min-width: 768px) {
      position: relative;
      display: flex;
      justify-content: space-between;
      align-items: center;

      padding-bottom: 137px;
   }

   @media screen and (min-width: 1366px) {
      padding-top: 54px;
      padding-bottom: 250px;
   }
}

.hero-content {
   margin-left: auto;
   margin-right: auto;

   @media screen and (max-width: 767px) {
      width: 280px;
      margin-bottom: 30px;
   }

   @media screen and (min-width: 768px) {
      width: 332px;
      margin-left: 0;
   }

   @media screen and (min-width: 1366px) {
      width: 520px;
   }

   .hero-text-block {
      margin-bottom: 52px;

      transform: translate(0px, 120%);
      opacity: 0;
      transition: all 1000ms ease 0ms;
   }

   .hero-text-block._active {
      transform: translate(0px, 0px);
      opacity: 1;
   }
}

.hero-title {
   margin-bottom: 8px;

   color: $primary-black-color;

   font-weight: 800;
   font-size: 20px;
   line-height: 1.3;
   letter-spacing: 0.03em;

   @media screen and (min-width: 768px) {
      font-size: 26px;
   }

   @media screen and (min-width: 1366px) {
      font-size: 58px;
   }
}

.hero-text {
   font-size: 14px;
   line-height: 1.57;
   color: $primary-text-color;

   @media screen and (min-width: 1366px) {
      font-size: 20px;
      line-height: 1.64;
   }
}

.hero-guarantee {
   display: block;
   margin-bottom: 52px;

   color: $primary-black-color;
   font-weight: 700;
   font-size: 14px;
   line-height: 1.57;

   @media screen and (min-width: 1366px) {
      font-size: 20px;
      line-height: 1.64;
   }
}

.hero-guarantee-accent {
   color: $accent-color;
}

.hero-content .btn-wrapper {
   @media screen and (min-width: 768px) {
      margin-left: 0;
   }
}

.hero-button-wrapper {
   opacity: 0;
   transition: opacity 1000ms ease 500ms;
}

.hero-button-wrapper._active {
   opacity: 1;
}

.hero-button {
   display: flex;
   align-items: center;
   justify-content: center;
}

.hero-button:hover {
   .button-text-mask {
      color: $accent-color;
   }
}

.hero-image {
   transform: scale(0);
   transition: transform 1000ms ease 0ms;
}

.hero-image-thumb._active .hero-image {
   transform: scale(1);
}

.hero-image {
   @media screen and (max-width: 767px) {
      width: 208px;
      margin: 0 auto;
   }

   @media screen and (min-width: 768px) {
      position: absolute;
      top: 0;
      right: 0;
      width: 376px;
   }

   @media screen and (min-width: 1366px) {
      width: 842px;
   }
}

sources.sass/layout/_advantages.scss:
.advantages {
  padding: 30px 0 44px; //was 45

  @media screen and (min-width: 768px) {
    padding: 46px 0;
  }

  @media screen and (min-width: 1366px) {
    padding: 97px 0 130px;
  }
}

.advantage-list {
  @media screen and (min-width: 768px) {
    display: flex;
  }
}

.advantage-item {
  position: relative;
  width: 215px;
  margin: 0 auto;

  color: $primary-black-color;

  @media screen and (max-width: 767px) {
    &:not(:last-child) {
      margin-bottom: 26px;
    }
  }

  @media screen and (min-width: 768px) and (max-width: 1365px) {
    width: calc(100% / 2 - 21px);

    &:not(:last-child) {
      margin-right: 21px;
    }
  }

  @media screen and (min-width: 1366px) {
    width: calc(100% / 3);
  }
}

.advantage-item {
  &::before {
    background-image: url(../images/icons/advantage-icon3.svg);
    content: '';
    position: absolute;
    left: 0;
    top: 0;

    width: 60px;
    height: 60px;

    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;

    @media screen and (min-width: 1366px) {
      background-image: url(../images/icons/advantage-icon3-desc.svg);
      width: 100px;
      height: 102px;
    }
  }
}

.advantage-item:nth-child(2) {
  &::before {
    background-image: url(../images/icons/advantage-icon2.svg);

    @media screen and (min-width: 1366px) {
      background-image: url(../images/icons/advantage-icon2-desc.svg);
    }
  }
}

.advantage-item:nth-child(3) {
  &::before {
    background-image: url(../images/icons/advantage-icon1.svg);

    @media screen and (min-width: 1366px) {
      background-image: url(../images/icons/advantage-icon1-desc.svg);
    }
  }
}

.advantage-title {
  display: flex;
  align-items: center;

  height: 60px;

  padding-left: 70px;

  font-size: 14px;
  font-weight: 700;
  line-height: 1.64;

  letter-spacing: 0.08em;

  @media screen and (min-width: 1366px) {
    font-size: 20px;
    padding-left: 90px;
    height: 102px;
  }
}

.advantage-list .advantage-item {
  transform: translateY(220%);
  opacity: 0;
  transition: transform 1000ms ease 0ms, opacity 1000ms ease 0ms;
}

.advantage-list._active .advantage-item {
  transform: translateY(0px);
  opacity: 1;
}

.advantage-list._active .advantage-item:nth-child(1) {
  transition: transform 1000ms ease 0ms, opacity 1000ms ease 300ms;
}

.advantage-list._active .advantage-item:nth-child(2) {
  transition: transform 1000ms ease 300ms, opacity 1000ms ease 600ms;
}

.advantage-list._active .advantage-item:nth-child(3) {
  transition: transform 1000ms ease 600ms, opacity 1000ms ease 900ms;
}

sources.sass/layout/_problems.scss:
.problems {
  padding-top: 25px; // was 50
  padding-bottom: 25px; // was 50
  background-image: linear-gradient(to bottom, $primary-black-color 75%, $primary-white-color 75%);
}

.problems-title {
  margin-bottom: 25px;
  font-size: 24px;
}

.problems-list-block {
  padding: 20px 14px 20px 30px;

  font-weight: 700;
  font-size: 14px;
  line-height: (20px / 14px);

  color: $primary-black-color;
  background-color: $primary-white-color;
  box-shadow: -5px 8px 26px rgba(0, 0, 0, 0.04);

  transform: translate(100%, 0px);
  opacity: 0;
  transition: transform 1000ms ease 0ms, opacity 1000ms ease 0ms;
}

.problems-list-block._active {
  transform: translate(0px, 0px);
  opacity: 1;
}

.problems-list {
  counter-reset: section;
}

.problems-item:not(:last-child) {
  margin-bottom: 15px;
}

.problems-item::before {
  counter-increment: section;
  content: '— 0' counter(section);

  display: block;
  margin-bottom: 5px;

  font-size: 14px;
  line-height: (17px / 14px); //121%

  color: $accent-color;
}

@media screen and (min-width: 768px) {
  .problems {
    padding-top: 60px;

    background-image: linear-gradient(
      to bottom,
      $primary-black-color 70%,
      $primary-white-color 70%
    );
  }

  .problems-list-block {
    padding: 30px 96px 26px 96px;
  }

  .problems-list {
    display: flex;
    flex-wrap: wrap;

    margin-top: -8px;
    margin-left: -22px;
  }

  .problems-item {
    margin-top: 8px;
    margin-left: 22px;
    flex-basis: calc((100% - 44px) / 2); // items margin 22px
  }

  .problems-item:not(:last-child) {
    margin-bottom: unset;
  }
}

@media screen and (min-width: 1366px) {
  .problems {
    padding-top: 100px;
    padding-bottom: 65px; // was 130

    background-image: url('../images/icons/campfire.svg'),
      linear-gradient(to bottom, $primary-black-color 80%, $primary-white-color 80%);
    background-repeat: no-repeat, no-repeat;
    background-position: right -46px top 146px, 0px 0px; // top was 94
    // background-position: 1273px 94px, 0px 0px;
  }

  .problems-title {
    margin-bottom: 70px;
    font-size: 46px;
  }

  .problems-list-block {
    padding: 70px 114px 62px 114px;

    font-size: 20px;
    line-height: (28px / 20px); // 140%
  }

  .problems-list {
    margin-top: -32px;
    margin-left: -40px;
  }

  .problems-item {
    margin-top: 32px;
    margin-left: 40px;
    flex-basis: calc((100% - 120px) / 3); // items margin 40px
  }

  .problems-item::before {
    margin-bottom: 10px;

    font-size: 20px;
    line-height: (33px / 20px); //165%
  }
}

sources.sass/layout/_how-we-do.scss:
.how-we-do {
  padding-top: 25px; // was 50
  padding-bottom: 50px;
}

.how-we-do-title {
  margin-bottom: 30px;

  text-align: center;
}

.how-we-do-list {
  text-align: center;

  transform: translate(-100%, 0px);
  opacity: 0;
  transition: transform 1000ms ease 0ms, opacity 1000ms ease 0ms;
}

.how-we-do-list._active {
  transform: translate(0px, 0px);
  opacity: 1;
}

.how-we-do-item {
  padding: 0 11px;
  // padding-bottom: 6px;

  width: 100%;
  height: 280px;

  background-color: $primary-white-color;
  box-shadow: 0px 8px 26px rgba(0, 0, 0, 0.1);

  transition: color 250ms, background-color 250ms;
}

.how-we-do-item:not(:last-child) {
  margin-bottom: 15px;
}

.how-we-do-icon-wrapper {
  display: flex;
  justify-content: center;
  align-items: center;

  height: 98px;
}

.how-we-do-icon {
  color: $accent-color;
  fill: currentColor;

  transition: color 250ms;
}

.how-we-do-item-title {
  margin-bottom: 5px;
  font-size: 14px;
  line-height: (20px / 14px); // 143%

  transition: color 250ms;
}

.title-span {
  display: block;
}

.how-we-do-item-title::after {
  content: '—';
  display: block;
  margin-top: 5px;

  font-size: 20px;
  line-height: (33px / 20px); // 165%
  color: $accent-color;

  transition: color 250ms;
}

.how-we-do-item:hover {
  color: $primary-white-color;
  background-color: $accent-color;
  cursor: pointer;
}

.how-we-do-item:hover .how-we-do-item-title::after,
.how-we-do-item:hover .how-we-do-item-title,
.how-we-do-item:hover .how-we-do-icon {
  color: $primary-white-color;
}

@media screen and (min-width: 768px) {
  .how-we-do-list {
    display: flex;
    flex-wrap: wrap;
    margin-top: -27px;
    margin-left: -20px;
  }

  .how-we-do-item {
    margin-top: 27px;
    margin-left: 20px;
    padding: 0 20px;
    // padding-bottom: 6px;

    flex-basis: calc((100% - 40px) / 2);
    height: 334px;
  }

  .how-we-do-item:not(:last-child) {
    margin-bottom: unset;
  }

  .how-we-do-icon-wrapper {
    height: 118px;
  }

  .how-we-do-item-title {
    margin-bottom: 10px;
  }

  .how-we-do-item-title::after {
    margin-top: 10px;
  }
}

@media screen and (min-width: 1366px) {
  .how-we-do {
    padding-top: 65px; //was 130
    padding-bottom: 130px;
  }

  .how-we-do-title {
    margin-bottom: 50px;

    text-align: unset;
  }

  .how-we-do-list {
    margin-top: -20px;
    margin-left: -20px;
  }

  .how-we-do-item {
    margin-top: 20px;
    margin-left: 20px;
    padding: 0 30px;
    padding-bottom: 55px;

    flex-basis: calc((100% - 60px) / 3);
    height: 396px;
  }

  .how-we-do-item-title {
    margin-bottom: unset;
    font-size: 20px;
    line-height: (28px / 20px); // 140%
  }

  .how-we-do-item-title::after {
    margin-top: unset;
  }

  .how-we-do-desc {
    font-size: 18px;
    line-height: (28px / 18px); // 156%
  }
}

sources.sass/layout/_program.scss:
//------------280-480px-MOBILE-----------------------------------
.program {
  margin-bottom: -1px;
  background-color: $primary-black-color;
  padding-top: 50px;
  padding-bottom: 25px;
  position: relative;
}
.program-title {
  font-weight: 800;
  font-size: 20px;
  line-height: 1.3;
  letter-spacing: 0.03em;
  text-align: center;
  color: $primary-white-color;
  margin-bottom: 43px;
}
.program-list {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-left: 63px;
  position: relative;
  &::after {
    content: '';
    display: block;
    position: absolute;
    background: url('../images/content/mobile/program-mob.png');
    background-size: contain;
    opacity: 0.5;
    width: 120px;
    height: 137px;
    bottom: -97px;
    right: -20px;
  }
}
@media (min-device-pixel-ratio: 2) and (max-width: 768px),
  (min-resolution: 192dpi) and (max-width: 768px),
  (min-resolution: 2dppx) and (max-width: 768px) {
  .program-list {
    &::after {
      background-image: url('../images/content/mobile/program-mob@2x.png');
    }
  }
}
.program-item {
  position: relative;
  padding-left: 20px;
  box-sizing: content-box;
  &:not(:last-child) {
    padding-bottom: 40px;
  }
  &:not(:last-child) {
    &::before {
      content: '';
      display: block;
      position: absolute;
      border-left: 1px solid $primary-white-color;
      transform: translate(-50%);
      left: 0;
      top: 10px;
      height: 100%;
      z-index: 1;
    }
  }
}
.program-item-title {
  font-weight: bold;
  font-size: 14px;
  line-height: 2;
  text-transform: uppercase;
  color: $primary-white-color;
  position: relative;
  margin-bottom: 5px;
  &::before {
    content: '';
    display: block;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background-color: $accent-color;
    position: absolute;
    bottom: 50%;
    transform: translate(-50%, 50%);
    left: -20px;
    z-index: 2;
  }
}
.program-study-block {
  width: 176px;
}
.program-study-block-item {
  font-size: 12px;
  line-height: 1.649;
  &:not(:first-child) {
    margin-top: 8px;
  }
}
.program-study-block-title {
  font-weight: bold;
  line-height: 1.649;
  color: $accent-color;
  margin-bottom: 5px;
  height: 32px;
  display: flex;
  align-items: center;
  letter-spacing: inherit;
}
.program-photo {
  margin-top: 10px;
}
.program-topic-item {
  font-weight: 500;
  font-size: 12px;
  line-height: 1.67;
  color: $primary-text-color;
  opacity: 0.5;
  letter-spacing: 0.08em;
}
//-----------TABLET-768px-------------------------
@media screen and (min-width: 768px) and (max-width: 1365px) {
  .program {
    padding-top: 60px;
    padding-bottom: 167px; // was 189
  }
  .program-title {
    font-size: 26px;
    text-align: left;
    margin-bottom: 52px;
  }
  .program-list {
    align-items: stretch;
    &::after {
      content: '';
      display: block;
      position: absolute;
      background-image: url('../images/content/tablet/program-tab.png');
      background-repeat: no-repeat;
      background-size: contain;
      width: 384px;
      height: 280px;
      transform: translateY(50%);
      bottom: 0;
      right: -40px;
    }
  }
  @media (min-device-pixel-ratio: 2) and (min-width: 768px),
    (min-resolution: 192dpi) and (min-width: 768px),
    (min-resolution: 2dppx) and (min-width: 768px) {
    .program-list {
      &::after {
        background-image: url('../images/content/tablet/program-tab@2x.png');
      }
    }
  }
  .program-item {
    padding-left: 56px;
  }
  .program-item-title {
    &::before {
      left: -56px;
    }
  }
  .program-study-block {
    display: flex;
  }
  .program-study-block :nth-child(1) {
    flex: none;
    width: 176px;
  }
  .program-study-block :nth-child(2) {
    width: 155px;
  }
  .program-study-block :nth-child(3) {
    width: 110px;
  }
  .program-study-block-mod {
    flex-direction: column;
  }
  .program-study-block-item {
    &:not(:first-child) {
      margin-top: 0px;
      margin-left: 50px;
    }
  }
}
//---------------1366px-desktop--------------
@media screen and (min-width: 1366px) {
  .program {
    padding-top: 120px;
    padding-bottom: 82px; // was 164
    position: relative;
    &::before {
      content: '';
      display: block;
      position: absolute;
      border-top: 1px solid $primary-white-color;
      transform: translateY(156px);
      width: 100vw;
      z-index: 1;
    }
  }
  .program-container {
    position: relative;
    &::after {
      content: '';
      display: block;
      position: absolute;
      background: url('../images/content/desktop/program-desk.png');
      background-size: cover;
      background-repeat: no-repeat;
      opacity: 0.5;
      width: 667px;
      height: 502px;
      bottom: -304px;
      left: -152px;
    }
  }
  @media (min-device-pixel-ratio: 2) and (min-width: 1366px),
    (min-resolution: 192dpi) and (min-width: 1366px),
    (min-resolution: 2dppx) and (min-width: 1366px) {
    .program-container {
      &::after {
        background-image: url('../images/content/desktop/program-desk@2x.png');
      }
    }
  }
  .program-title {
    font-size: 46px;
    line-height: 1.3;
    letter-spacing: 0.03em;
    text-align: left;
    height: 57px;
    margin-bottom: 100px;
  }
  .program-list {
    flex-direction: row;
    margin-left: 0px;
    justify-content: space-between;
    align-items: start;

    &::after {
      content: 'none';
      display: none;
    }
  }
  .program-item {
    padding-top: 60px;
    padding-left: 0px;
    box-sizing: content-box;
    width: 240px;
    &:not(:last-child) {
      padding-bottom: 0px;
    }
    &:not(:last-child) {
      &::before {
        content: 'none';
        display: none;
      }
    }
  }
  .program-item-title {
    font-size: 20px;
    line-height: 1.4;
    text-transform: none;
    height: 28px;
    &::first-letter {
      font-size: 30px;
      line-height: 0.93;
    }
    &::before {
      content: '';
      display: block;
      width: 12px;
      height: 12px;
      border-radius: 50%;
      background-color: $accent-color;
      position: absolute;
      top: -60px;
      bottom: auto;
      transform: translate(0%, -50%);
      left: auto;
      z-index: 2;
    }
  }
  .program-study-block {
    width: 240px;
    display: block;
  }
  .program-study-block-title {
    font-size: 20px;
    display: block;
    margin-bottom: 10px;
  }
  .program-study-block-item {
    margin-top: 25px;
    &:not(:first-child) {
      margin-top: 25px;
      margin-left: 0px;
    }
  }
  .program-topic-item {
    font-size: 18px;
    line-height: 1.56;
    color: $primary-text-color;
    letter-spacing: 0.08em;
  }
}

sources.sass/layout/_benefits.scss:
