  .guar {
    padding-top: 65px; // was not
    padding-bottom: 65px; // was not
  }

  .guar-list {
    padding: 50px 112px 45px 112px;
  }
  .guar-titel {
    font-size: 46px;
    margin-bottom: 50px;
  }
  .guar-item {
    font-size: 20px;
    line-height: 1.4;

    &::before {
      width: 22px;
      height: 30px;
      left: -11px;
    }

    &:not(:last-child) {
      padding-bottom: 20px;
    }
  }
}

.guar-list {
  transform: scale(2);
  opacity: 0;
  transition: transform 1000ms ease 0ms, opacity 1000ms ease 100ms;
}

.guar-list._active {
  transform: scale(1);
  opacity: 1;
}

